import umg.edu.gt.model.MediaPlayer;
import umg.edu.gt.model.PlaylistManager;

public class Main 
{
    public static void main( String[] args )
    {
        PlaylistManager manager = new PlaylistManager();

        // Priority 1 = High, Priority 2 = Normal
        manager.addSong(new Song("Glimpse of us", "Joji", 30, 1));
        manager.addSong(new Song("Mania Cardiaca", "Enjambre", 15, 2));
        manager.addSong(new Song("MELT", "MASS OF FERMENTING DREGS", 20, 1));

        QueueLinked<Song> finalQueue = manager.getOrderedQueue();

        MediaPlayer player = new MediaPlayer(finalQueue);
        player.reproduce(finalQueue);
    }
}
